pragma solidity ^0.5.8;

contract slotMachine {


    address private _player;
 
    uint256 public contractBalance;
    uint256 private _userBalance;
    uint256 private _ethLimit = 1000 wei;
    uint8 private _randNonce;

    string private _dMsg;

    ///-----------event logging-------------///
    event PlayerDeposit(address Contract, address Player, uint256 Amount);


    ///***********Modifiers***********///

    
    //make sure address is Player
    modifier isPlayer() {
        require(msg.sender == _player, "Only Player can use this function.");
        _;
    }

    ///******constructor********///
    constructor() public {
        _randNonce = 1;
        _userBalance = 0;
    }   
    function payContract() public payable returns (string memory) {
        
        
        //make sure contract cannot accept more than ether limit
        require((_userBalance + msg.value) <= _ethLimit, "Too much Ether!");
        
        _userBalance += msg.value;
        
        //Register player's address
        _player = msg.sender;
        
        //log event    
        emit PlayerDeposit(address(this), msg.sender, msg.value);
        
        _dMsg = "Contract Paid.";
            
        return _dMsg;
    }

    function payForIt() internal returns (string memory) {
        
        uint256 winBalance = _userBalance * 2;
        if(contractBalance < winBalance){
            winBalance = contractBalance;
        }
        msg.sender.transfer(winBalance); 
        contractBalance = address(this).balance;
        _dMsg = " --> Get paid, winner!";
        return _dMsg;
    }
    
    ///******Start up machine********///
    function start() isPlayer public returns (bool, uint8[] memory) {
        //Check whether user bet any ether
        uint8[] memory randomNumberList = new uint8[](3);
        bool win = false;
        require(_userBalance > 0);

        //generate three output of slot machine
        uint8 randomValue1 = randomNumber(_randNonce);
        randomNumberList[0] = randomValue1;
        _randNonce += 1;
        uint8 randomValue2 = randomNumber(_randNonce);
        randomNumberList[1] = randomValue2;
        _randNonce += 1;
        uint8 randomValue3 = randomNumber(_randNonce);
        randomNumberList[2] = randomValue3;
        _randNonce += 1;

        if(randomValue1 == randomValue2 && randomValue2 == randomValue3)
        {    
            payForIt();
            win = true;        
        }
        
    }
    ///*********************Generate Random number************************///
    function randomNumber(uint8 _nonce) internal returns (uint8) {
        //reset nonce when reach a certain number to make it unpredictable
        if(_nonce > 179) {
            _randNonce = 1;
        }
        
        return uint8(uint(keccak256(abi.encodePacked(blockhash(block.number - 1), _nonce)))%10); // 1-10
    }


}
